if("geolocation"in navigator){$('.js-geolocation').show();}else{$('.js-geolocation').hide();}
$('.js-geolocation').on('click',function(){navigator.geolocation.getCurrentPosition(function(position){loadWeather(position.coords.latitude+','+position.coords.longitude);});});$(document).ready(function(){loadWeather('Villa Tulumaya','20070715');});function loadWeather(location,woeid){$.simpleWeather({location:location,woeid:woeid,unit:'c',success:function(weather){html='<h6> <i class="icon-'+weather.code+'"></i> '+weather.temp+'&deg;'+weather.units.temp+'</h6>';$("#weather").html(html);},error:function(error){$("#weather").html('<p>'+error+'</p>');}});};